import React, { useState } from 'react';
import axios from 'axios';
import styles from './HorarioForm.module.css'; // Importa o CSS como um módulo

const HorarioForm = ({ onSubmitSuccess }) => {
  const [name, setName] = useState('');
  const [horarios, setHorarios] = useState(''); // Atualiza o campo para 'horarios'
  const [category, setCategory] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !horarios || !category) {
      setErrorMessage('Todos os campos são obrigatórios');
      return;
    }

    try {
      const response = await axios.post('/horarios', {
        name,
        horarios, // Envia os horários no lugar do orçamento
        category,
      });

      setName('');
      setHorarios(''); // Limpa o campo de horários
      setCategory('');
      setErrorMessage(''); 
      if (onSubmitSuccess) {
        onSubmitSuccess(response.data); 
      }
    } catch (error) {
      console.error('Erro ao salvar o horário:', error);
      setErrorMessage('Erro ao salvar o horário. Tente novamente.');
    }
  };

  return (
    <div className={styles['horario-form-container']}>
      <h2>Cadastrar Horário</h2>
      <form onSubmit={handleSubmit} className={styles['horario-form']}>
        <div className={styles['form-group']}>
          <label htmlFor="name">Nome:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Digite o nome"
            required
            className={styles['form-input']}
          />
        </div>
        <div className={styles['form-group']}>
          <label htmlFor="horarios">Horários:</label>
          <input
            type="datetime-local" // Campo que aceita datas e horários
            id="horarios"
            value={horarios}
            onChange={(e) => setHorarios(e.target.value)}
            required
            className={styles['form-input']}
          />
        </div>

        <div className={styles['form-group']}>
          <label htmlFor="category">Categoria:</label>
          <select
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
            className={styles['form-input']}
          >
            <option value="">Selecione uma categoria</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Prova">Prova</option>
            <option value="Orientação">Orientação</option>
            <option value="Co-Orientação">Co-Orientação</option>
          </select>
        </div>

        {errorMessage && <p className={styles['error-message']}>{errorMessage}</p>}

        <button type="submit" className={styles['btn-submit']}>Salvar Horário</button>
      </form>
    </div>
  );
};

export default HorarioForm;
