import { Link } from 'react-router-dom';
import styles from './HorarioCard.module.css';
import { BsPencil, BsFillTrashFill } from 'react-icons/bs';

function HorarioCard({ id, name, horario, category, handleRemove }) {
  const remove = (e) => {
    e.preventDefault();
    handleRemove(id);
  };

  return (
    <div className={styles.project_card}>
      <h4>{name}</h4>
      <p>
        <span>Horário:</span> {horario}
      </p>
      {/* Exibir categoria ou "Sem categoria" se não existir */}
      <p className={styles.category_text}>
        {category && typeof category === 'object' && category.name ? (
          <span className={`${styles[category.name.toLowerCase()] || styles.default}`}></span>
        ) : (
          <span className={styles.default}></span>
        )}
        {category && typeof category === 'object' && category.name ? category.name : 'Sem categoria'}
      </p>
      <div className={styles.project_card_actions}>
        <Link to={'/horario/' + id}>
          <BsPencil /> Editar
        </Link>
        <button onClick={remove}>
          <BsFillTrashFill />
          Excluir
        </button>
      </div>
    </div>
  );
}

export default HorarioCard;
