import React from 'react';
import HorarioForm from '../horario/HorarioForm';
import styles from './newHorario.module.css'; 
import Message from '../layout/Message';

function NewHorario() {
  const [message, setMessage] = useState('');
  const [type, setType] = useState('success');

  const createHorario = async (horario) => {
    const res = await fetch('http://localhost:3000/horarios', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(horario),
    });

    const data = await res.json();

    if (res.ok) {
      setMessage('Horário criado com sucesso!');
      setType('success');
    } else {
      setMessage('Erro ao criar o horário.');
      setType('error');
    }
  };

  return (
    <div className={styles.container}>
      <h1>Criar Horário</h1>
      {message && <Message type={type} msg={message} />}
      <HorarioForm handleSubmit={createHorario} btnText="Criar horário" />
    </div>
  );
}

export default NewHorario;
