function HorarioCard({ id, name, budget, category, handleRemove }) {
  const remove = (e) => {
    e.preventDefault();
    handleRemove(id);
  };

  return (
    <div className={styles.project_card}>
      <h4>{name}</h4>
      <p>
        <span>Horário:</span> R${budget} {/* Corrigido para ser mais informativo */}
      </p>
      <p className={styles.category_text}>
        <span className={`${styles[category?.toLowerCase()] || styles.defaultCategory}`}></span> 
        {category || 'Categoria não definida'}
      </p>
      <div className={styles.project_card_actions}>
        <Link to={'/horario/' + id}>
          <BsPencil /> Editar
        </Link>
        <button onClick={remove}>
          <BsFillTrashFill />
          Excluir
        </button>
      </div>
    </div>
  );
}

export default HorarioCard;
