import React, { useState, useEffect } from 'react';

function HorarioForm({ handleSubmit, btnText }) {
  const [horario, setHorario] = useState({
    name: '',
    budget: '',
    category: { id: '', name: '' }, // Esta parte está sendo utilizada corretamente
  });
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/categories', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((resp) => resp.json())
      .then((data) => {
        setCategories(data);
      })
      .catch((err) => console.log(err));
  }, []);

  const handleChange = (e) => {
    setHorario({
      ...horario,
      [e.target.name]: e.target.value,
    });
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = categories.find((category) => category._id === e.target.value);
    setHorario({
      ...horario,
      category: {
        id: selectedCategory._id,
        name: selectedCategory.name,
      },
    });
  };

  const submit = (e) => {
    e.preventDefault();
    handleSubmit(horario);
  };

  return (
    <form onSubmit={submit}>
      <input
        type="text"
        name="name"
        placeholder="Digite o nome do horário"
        onChange={handleChange}
      />
      <input
        type="number"
        name="budget"
        placeholder="Digite o orçamento"
        onChange={handleChange}
      />
      <select name="category" onChange={handleCategoryChange}> {/* Mudado de category_id para category */}
        <option>Selecione uma categoria</option>
        {categories.map((category) => (
          <option key={category._id} value={category._id}>
            {category.name}
          </option>
        ))}
      </select>
      <button type="submit">{btnText}</button>
    </form>
  );
}

export default HorarioForm;
