import React, { useState } from 'react';
import axios from 'axios';
import './HorarioForm.module.css';

const HorarioForm = ({ onSubmitSuccess }) => {
  const [name, setName] = useState('');
  const [budget, setBudget] = useState('');
  const [category, setCategory] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !budget || !category) {
      setErrorMessage('Todos os campos são obrigatórios');
      return;
    }

    try {
      const response = await axios.post('/horarios', {
        name,
        budget: parseFloat(budget), 
        category,
      });

      setName('');
      setBudget('');
      setCategory('');
      setErrorMessage(''); 
      if (onSubmitSuccess) {
        onSubmitSuccess(response.data); 
      }
    } catch (error) {
      console.error('Erro ao salvar o horário:', error);
      setErrorMessage('Erro ao salvar o horário. Tente novamente.');
    }
  };

  return (
    <div className="horario-form-container">
      <h2>Cadastrar Horário</h2>
      <form onSubmit={handleSubmit} className="horario-form">
        <div className="form-group">
          <label htmlFor="name">Nome:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Digite o nome"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="budget">Orçamento:</label>
          <input
            type="number"
            id="budget"
            value={budget}
            onChange={(e) => setBudget(e.target.value)}
            placeholder="Digite o valor do orçamento"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Categoria:</label>
          <select
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          >
            <option value="">Selecione uma categoria</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Prova">Prova</option>
            <option value="Orientação">Orientação</option>
            <option value="Co-Orientação">Co-Orientação</option>
          </select>
        </div>

        {errorMessage && <p className="error-message">{errorMessage}</p>}

        <button type="submit" className="btn-submit">Salvar Horário</button>
      </form>
    </div>
  );
};

export default HorarioForm;
