import { Link } from 'react-router-dom';
import { BsPencil, BsFillTrashFill } from 'react-icons/bs';
import styles from './HorarioCard.module.css'; 

function HorarioCard({ id, name, budget, category, handleRemove }) {
  const remove = (e) => {
    e.preventDefault();
    handleRemove(id);
  };

  return (
    <div className={styles.card}>
      <h4>{name}</h4>
      <p>
        <span>Horario:</span> {budget}
      </p>
      {category ? (
        <p>
          <span>{category.name}</span>
        </p>
      ) : (
        <p>Categoria n√£o definida</p>
      )}
      <div>
        <Link to={'/horario/' + id} className={styles.editLink}>
          <BsPencil /> Editar
        </Link>
        <button onClick={remove} className={styles.deleteButton}>
          <BsFillTrashFill />
          Excluir
        </button>
      </div>
    </div>
  );
}

export default HorarioCard;
