import React, { useState, useEffect } from 'react';
import styles from './Login.module.css'; 
import { useHistory } from 'react-router-dom'; // Para navegação entre páginas

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loggedInUsername, setLoggedInUsername] = useState(null);
  const history = useHistory(); // Navegação programática

  useEffect(() => {
    const loggedInStatus = localStorage.getItem('isLoggedIn');
    const storedUsername = localStorage.getItem('username');

    if (loggedInStatus === 'true' && storedUsername) {
      setIsLoggedIn(true);
      setLoggedInUsername(storedUsername);
    }
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Erro ao efetuar login');
      }

      setIsLoggedIn(true);
      setLoggedInUsername(username);
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('username', username);
      alert('Login bem-sucedido!');
    } catch (error) {
      alert(error.message);
    }
  };

  const handleForgotPassword = () => {
    history.push('/forgot-password'); 
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('username');
  };

  return (
    <div className={styles.logincontainer}>
      <h2>{isLoggedIn ? 'Bem-vindo!' : 'Login'}</h2>
      {isLoggedIn ? (
        <div>
          <p>Bem-vindo, {loggedInUsername}!</p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
          <br />
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
          <br />
          <button type="submit">Login</button>
          <button
            type="button"
            onClick={handleForgotPassword}
            className={styles.forgotPasswordButton}
          >
            Esqueceu a Senha?
          </button>
        </form>
      )}
    </div>
  );
};

export default Login;
