import { useState } from 'react';

function HorarioForm({ handleSubmit, btnText }) {
  const [name, setName] = useState('');
  const [budget, setBudget] = useState('');
  const [category, setCategory] = useState({ id: '', name: '' }); // Estado para categoria

  const submit = (e) => {
    e.preventDefault();
    handleSubmit({ name, budget, category }); // Passa os dados do formulário para a função handleSubmit
  };

  return (
    <form onSubmit={submit}>
      <label>
        Nome:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </label>
      <label>
        Orçamento:
        <input type="number" value={budget} onChange={(e) => setBudget(e.target.value)} required />
      </label>
      <label>
        Categoria:
        <input 
          type="text" 
          value={category.name} 
          onChange={(e) => setCategory({ ...category, name: e.target.value })} 
          required 
        />
      </label>
      <button type="submit">{btnText}</button>
    </form>
  );
}

export default HorarioForm;
