import React, { useState } from 'react';
import styles from './Select.module.css';

const Select = () => {
  const [selectedOption, setSelectedOption] = useState('');

  const options = [
    { id: 1, name: 'Trabalho' },
    { id: 2, name: 'Prova' },
    { id: 3, name: 'Orientação' },
    { id: 4, name: 'Co-Orientação' },
  ];

  const handleSelectChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleSubmit = async () => {
    if (!selectedOption) {
      alert('Selecione uma opção antes de enviar!');
      return;
    }

    const selectedCategory = options.find(option => option.id === parseInt(selectedOption));

    const data = {
      name: 'Nome do Horário', // Ajuste conforme necessário
      budget: 100, // Ajuste conforme necessário
      category: { name: selectedCategory.name }
    };

    try {
      const response = await fetch('/horarios', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();
      console.log('Horário criado:', result);
    } catch (error) {
      console.error('Erro ao criar horário:', error);
    }
  };

  return (
    <div className={styles.form_control}>
      <label htmlFor="mySelect">Escolha o Tipo de Trabalho:</label>
      <select id="mySelect" value={selectedOption} onChange={handleSelectChange}>
        <option value="">Selecione uma opção</option>
        {options.map(option => (
          <option value={option.id} key={option.id}>
            {option.name}
          </option>
        ))}
      </select>
      <button onClick={handleSubmit}>Enviar</button>
    </div>
  );
};

export default Select;
